mapper.select:
Select company.companyName
from Company company, Users user
where company.companyID=user.companyID and user.userID = #{userID};

Select company.companyID
from Company company, Users user
where company.companyID=user.companyID and user.userID = #{userID};

SELECT post.postID,post.userID, user.userName,post.boardID,post.companyID,post.postTitle,post.postContent,post.postRegisterTime
FROM Post post, Users user
WHERE post.userID = user.userID and post.boardID=#{boardID}
ORDER BY post.postID DESC;

SELECT post.postID,post.userID, user.userName,post.boardID,post.companyID,post.postTitle,post.postContent,post.postRegisterTime
FROM Post post, Users user
WHERE post.userID = user.userID and post.postID=#{postID}

SELECT userType
FROM Users
WHERE userID = #{userID};
