mapper.select:
SELECT Count(comment_id)
FROM comments
WHERE post_id = #{postID};

SELECT Count(comment_id)
FROM comments
WHERE post_id = #{postID}
  AND comment_referenced_id IS NULL;

SELECT user_id
FROM comments
WHERE comment_id = #{commentID};

mapper.insert:
INSERT INTO comments (post_id, user_id, company_id, comment_content, comment_content_except_htmltag, comment_referenced_id) VALUES (#{postID}, #{userID}, #{companyID}, #{commentContent}, #{commentContentExceptHTMLTag}, NULL);
SELECT #{commentID};
SELECT LAST_INSERT_ID();

mapper.update:
UPDATE comments
SET comment_content = #{commentContent},
    comment_content_except_htmltag = #{commentContentExceptHTMLTag}
WHERE comment_id = #{commentID};

UPDATE comments
SET replies_count = replies_count + 1
WHERE comment_id = #{commentReferencedID};

UPDATE comments
SET replies_count = replies_count - 1
WHERE comment_id = #{commentReferencedID};

mapper.select:
SELECT comment_referenced_id
FROM comments
WHERE comment_id = #{commentID};

SELECT replies_count
FROM comments
WHERE comment_id = #{commentReferencedID};
