mapper.select:
SELECT file_id AS fileID, post_id AS postID, comment_id AS commentID, resource_url AS resourceUrl, origin_file_name AS originFileName, stored_file_name AS storedFileName, file_size AS fileSize, upload_time AS uploadTime FROM files
WHERE post_id = #{postID};
WHERE comment_id = #{commentID};

mapper.update:
UPDATE files

  SET    post_id = #{postID}


  SET    comment_id = #{commentID}

WHERE  stored_file_name = #{storedFileName};
