mapper.select:
SELECT user_name as userName
FROM users
WHERE user_id = #{userId};

SELECT user_type AS userType
FROM users
WHERE user_id = #{userId};

mapper.update:
UPDATE users
SET    image_url = #{imageUrl},
       image_file_name = #{imageFileName},
       thumbnail_url = #{thumbnailUrl},
       thumbnail_file_name = #{thumbnailFileName}
WHERE  user_id = #{userId};
